mapper.select:
SELECT
COUNT(0)
FROM
sys_config
WHERE
group_name =#{group}

	and state = #{state}


	and sc_value like CONCAT ('%',#{scvalue},'%')


	and create_time >=#{startTime}


	and create_time <=#{endTime}

SELECT

FROM
sys_config
WHERE
group_name =#{group}

	and state = #{state}


	and sc_value like CONCAT ('%',#{scvalue},'%')


	and create_time >=#{startTime}


	and create_time <=#{endTime}

order by create_time asc limit
#{pageStartNumber},#{pageEndNumber}

select

from sys_config
where group_name = #{groupName}
order by create_time asc limit
#{pageStartNumber},#{pageEndNumber}

mapper.update:
update sys_config
set state = #{state,jdbcType=INTEGER}
where id = #{id,jdbcType=BIGINT}

update sys_config
set sc_value = #{scValue}
where sc_key = #{scKey}

