mapper.select:
  	SELECT

  	FROM
  	    cms_news
  	WHERE 1=1

		AND cms_news.status = #{state}


        AND cms_news.title LIKE CONCAT ('%',#{title},'%')	


        AND cms_news.type = #{newsType}	


		AND cms_news.create_time >=#{startTime}


		AND cms_news.create_time <=#{endTime}

  	ORDER BY
  		cms_news.order_num ASC , cms_news.update_time DESC
  	limit
		#{startNumber},#{endNumber}

  	SELECT
		count(cms_news.id)
	FROM
		cms_news
	WHERE 1=1

		AND cms_news.status = #{state}


        AND cms_news.title LIKE  CONCAT ('%',#{title},'%')


        AND cms_news.type = #{newsType}	


		AND cms_news.create_time >=#{startTime}


		AND cms_news.create_time <=#{endTime}

SELECT
FROM cms_news WHERE 1=1
AND cms_news.title = #{title,jdbcType=VARCHAR}
