mapper.select:
SELECT

,

FROM
t_products
WHERE
1=1

	and title like CONCAT ('%',#{title},'%')


	AND status in (1,2,4,5)


	AND status in (0)


	AND status in (3)


	AND status = #{status}


	and create_date >=#{startTime}


	and create_date <=#{endTime}


	and recommend =#{recommend,jdbcType=INTEGER}


	and products_id like CONCAT ('%',#{productsId},'%')


	and oper_id =#{operId}

order by create_date Desc limit
#{pageStartNumber},#{pageEndNumber}

SELECT
COUNT(0)
FROM
t_products
WHERE
1=1

	and title like CONCAT ('%',#{title},'%')


	AND status in (1,2,4,5)


	AND status in (0)


	AND status in (3)


	AND status = #{status}


	and create_date >=#{startTime}


	and create_date <=#{endTime}


	and recommend =#{recommend,jdbcType=INTEGER}


	and products_id like CONCAT ('%',#{productsId},'%')


	and oper_id =#{operId}

SELECT tp.products_id as products_id, tp.title as title, tp.country as country, tp.position as position, tp.stock as stock, tp.personal as personal, tp.sales as sales, tp.distributor as distributor, tp.china_price as china_price, tp.car_price as car_price, tpi.file_path as file_path FROM t_products tp LEFT JOIN t_products_image tpi ON tp.products_id = tpi.product_id LEFT JOIN sys_config sys ON tp.brand_id = sys.id WHERE tp.`status` = 2 and tpi.type = 1 AND sys.state = 0
and tp.brand_id = #{brandId}
and title like CONCAT ('%',#{title},'%')
AND tp.car_type = #{carType}
AND tp.price_range = #{priceRange}
ORDER BY tp.update_date DESC
ORDER BY tp.car_price DESC ,tp.update_date desc
ORDER BY tp.car_price , tp.update_date desc
LIMIT #{pageStartNumber},#{pageEndNumber}

SELECT COUNT(1) FROM t_products tp LEFT JOIN t_products_image tpi ON tp.products_id = tpi.product_id LEFT JOIN sys_config sys ON tp.brand_id = sys.id WHERE tp.`status` = 2 and tpi.type = 1 AND sys.state = 0
and title like CONCAT ('%',#{title},'%')
and tp.brand_id = #{brandId}
AND tp.car_type = #{carType}
AND tp.price_range = #{priceRange}

SELECT

,

FROM
t_products
WHERE
car_type = #{tProductsTemp.carType}
and
position = #{tProductsTemp.position}
and
car_model = #{tProductsTemp.carModel}
and
out_color = #{tProductsTemp.outColor}
and
in_color = #{tProductsTemp.inColor}
and
car_standards =#{tProductsTemp.carStandards}
and
country =#{tProductsTemp.country}
and
power =#{tProductsTemp.power}
and
engine = #{tProductsTemp.engine}
and
factory_date =#{tProductsTemp.factoryDate}
and
doors =#{tProductsTemp.doors}
and
seats =#{tProductsTemp.seats}
ORDER BY create_date 
LIMIT 1
